@import url('https://fonts.googleapis.com/css2?family=Source+Code+Pro:wght@300;400;600&display=swap');
@import url('./market.css');
@import url('./social.css');

:root {
  --color-primary-0b: #000;	
  --color-primary-0: #FFFAF6;	
  --color-primary-1: #B9A08D;
  --color-primary-2: #E4CCB9;
  --color-primary-3: #FFFAF6;
  --color-primary-4: #EEE9E6;

  --color-secondary-1-0: #D1D4D7;
  --color-secondary-1-1: #5F6A7A;
  --color-secondary-1-2: #7C8796;
  --color-secondary-1-3: #A8AAAE;
  --color-secondary-1-4: #97999C;

  --color-secondary-2-0: #DFE7DF;
  --color-secondary-2-1:  #6F9271;
  --color-secondary-2-2: #92B394;
  --color-secondary-2-2: #C5CDC6;
  --color-secondary-2-4: #B5BBB5;

  --color-computer-green: #32cd32;
  --color-computer-red: #ff0000;
  --color-coin-green: #008000;
  --color-coin-altgreen: #adff2f;
}

html {
  box-sizing: border-box;
}

body {
  margin: 0;
  /* background: #000; */
  font-size: 14px;
  font-family: Source Code Pro, 'Courier New', Courier, monospace, sans-serif;
}

.limegreen {
  color: limegreen;
}

.menu-bars {
  position: fixed;
  top: 1rem;
  right: 2rem;
  z-index: 10;
  display: inline;
  cursor: pointer;
}

.bar1,
.bar2,
.bar3 {
  width: 35px;
  height: 2px;
  background-color: limegreen;
  margin: 8px 0;
  transition: 0.4s;
}

.change .bar1 {
  transform: rotate(-45deg) translate(-7px, 8px);
}

.change .bar2 {
  opacity: 0;
}

.change .bar3 {
  transform: rotate(45deg) translate(-6px, -8px);
}

.overlay {
  position: fixed;
  z-index: 9;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  background-color: rgba(0, 0, 0, 0.5);
  transform: translateX(-100vw);
}

.overlay-slide-in {
  transition: all 0.4s ease-in-out;
  transform: translateX(0);
}

.overlay-slide-out {
  transition: all 0.8s ease-in-out;
  transform: translateX(-100vw);
}

@keyframes slide-in {
  from {
    transform: translate(-100vw);
  }

  to {
    transform: translate(0);
  }
}

@keyframes slide-out {
  from {
    transform: translate(0);
  }

  to {
    transform: translate(-100vw);
  }
}

.nav-1-slide-in {
  animation: slide-in 0.2s linear 0.1s both;
}

.nav-2-slide-in {
  animation: slide-in 0.2s linear 0.2s both;
}

.nav-3-slide-in {
  animation: slide-in 0.2s linear 0.3s both;
}

.nav-4-slide-in {
  animation: slide-in 0.2s linear 0.4s both;
}

.nav-5-slide-in {
  animation: slide-in 0.2s linear 0.5s both;
}

.nav-6-slide-in {
  animation: slide-in 0.2s linear 0.6s both;
}

.nav-1-slide-out {
  animation: slide-out 0.2s linear 0.6s both;
}

.nav-2-slide-out {
  animation: slide-out 0.2s linear 0.5s both;
}

.nav-3-slide-out {
  animation: slide-out 0.2s linear 0.4s both;
}

.nav-4-slide-out {
  animation: slide-out 0.2s linear 0.3s both;
}

.nav-5-slide-out {
  animation: slide-out 0.2s linear 0.2s both;
}

.nav-6-slide-out {
  animation: slide-out 0.2s linear 0.1s both;
}



nav,
nav ul {
  height: 100vh;
  margin: 0;
  padding: 0;
  text-align: center;
}

nav ul {
  display: flex;
  flex-direction: column;
  justify-content: stretch;
  list-style: none;
}

.nav-links li {
  height: 17%;
  overflow: hidden;
}

.nav-links a::before {
  content: "";
  width: 25vw;
  height: 4px;
  background-color: limegreen;
  position: absolute;
  left: 0;
  top: 45%;
  opacity: 0;
  transition: opacity 200ms ease-in-out;
}

.nav-links a::after {
  content: "";
  width: 25vw;
  height: 4px;
  background-color: limegreen;
  position: absolute;
  right: 0;
  top: 45%;
  opacity: 0;
  transition: opacity 200ms ease-in-out;
}


.nav-links a:hover::before {
  opacity: 1;
}

.nav-links a:hover::after {
  opacity: 1;
}

.nav-links li a {
  text-decoration: none;
  position: relative;
  top: 40%;
  display: block;
  color: var(--color-primary-0);
  color: black;
  font-size: 1rem;
  text-transform: uppercase;
  letter-spacing: 4px;
  transition: color 200ms ease-in-out, letter-spacing 200ms ease-in-out;
}

.nav-links a:hover {
  color: limegreen;
  letter-spacing: 6px;
}

.nav-links li:nth-child(1) {
  background-color: var(--color-secondary-1-0);
}

.nav-links li:nth-child(2) {
  background-color: var(--color-secondary-1-1);
}

.nav-links li:nth-child(3) {
  background-color: var(--color-secondary-1-2);
}

.nav-links li:nth-child(4) {
  background-color: var(--color-secondary-2-1);
}

.nav-links li:nth-child(5) {
  background-color: var(--color-primary-2);
}

.nav-links li:nth-child(6) {
  background-color: var(--color-secondary-1-4);
}


section {
  width: 100%;
  height: 100vh;
  position: relative;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  text-transform: uppercase;
  letter-spacing: 2px;
  color: var(--color-primary-0);
}

section#home {
  background-image: url("../img/chain.jpg");
  background-size: cover;
  background-position: center center;
}

section#market {
  background-color: var(--color-secondary-1-0);
}

section#inventory {
  background-color: var(--color-secondary-1-1);
}

section#stats {
  background-color: var(--color-secondary-1-2);
}

section#news {
  background-color: var(--color-secondary-1-3);
}

section#settings {
  background-color: var(--color-secondary-1-4);
}

@media (max-width: 600px) {
  section#home a {
    top: 1rem;
    left: 1rem;
  }

  .menu-bars {
    top: 0.5rem;
    right: 1rem;
  }
}
